mapper.insert:
INSERT INTO files (
                    file_id
                  , folder_id
                  , user_code
                  , origin_name
                  , stored_name
                  , extension
                  , file_size
                  , storage_path
                  , last_modified_at
) VALUES (
           uuid_in(md5(random()::text || now()::text)::cstring)
         , #{folderId}
         , #{userCode}
         , #{originName}
         , #{storedName}
         , #{extension}
         , #{fileSize}
         , #{storagePath}
         , #{lastModifiedAt}
         )
